mapper.select:


     and usual_por = #{usualPor}
     and usual_sco = #{usualSco}
     and mid_por = #{midPor}
     and mid_sco = #{midSco}
     and final_por = #{finalPor}
     and final_sco = #{finalSco}
     and others = #{others}
     and choose_id = #{chooseId}
    and S.choose_id = CH.id and
    C.id = CH.course_id

group by S.id

mapper.update:
update score_info
usual_por = #{usualPor}, usual_sco = #{usualSco}, mid_por = #{midPor}, mid_sco = #{midSco}, final_por = #{finalPor}, final_sco = #{finalSco}, others = #{others}, choose_id = #{chooseId},
where id = #{id}
