select.where:
 and name like concat('%', #{name}, '%')
 and adm_method = #{admMethod}
 and in_school = #{inSchool}
 and class_id = #{classId}
 and major_id = #{majorId}and
stu_info.id = sys_user.user_id
and sys_user.status = 0
and sys_user.del_flag = '0'

mapper.select:

where stu_info.id = #{id}
and stu_info.id = sys_user.user_id
and sys_user.status = 0

mapper.update:
update stu_info
name = #{name}, type = #{type}, adm_method = #{admMethod}, in_school = #{inSchool}, stu_status = #{stuStatus}, class_id = #{classId}, major_id = #{majorId},
where id = #{id}

update sys_user
set user_name = #{user_name}
where user_id = #{id}
